const emotionColors = {
  happy: { bg: '#FFF9C4', text: '#B7940A', icon: '#FBC02D' },
  surprised: { bg: '#E1BEE7', text: '#6A1B9A', icon: '#8E24AA' },
  bad: { bg: '#F5F5F5', text: '#424242', icon: '#616161' },
  fearful: { bg: '#FFE0B2', text: '#E65100', icon: '#FB8C00' },
  angry: { bg: '#FFCDD2', text: '#B71C1C', icon: '#D32F2F' },
  disgusted: { bg: '#C8E6C9', text: '#1B5E20', icon: '#388E3C' },
  sad: { bg: '#BBDEFB', text: '#0D47A1', icon: '#1976D2' }
};

const emotionIcons = {
  happy: FaSmile, surprised: FaSurprise, bad: FaMeh, fearful: FaFrown, angry: FaAngry, disgusted: FaDizzy, sad: FaSadTear
};

function getColor(emotion, parentEmotion) {
  if (emotionColors[emotion]) {
    return emotionColors[emotion];
  }
  if (parentEmotion && emotionColors[parentEmotion]) {
    return emotionColors[parentEmotion];
  }
  return { bg: '#F5F5F5', text: '#424242', icon: '#616161' };
}

function EmotionButton({ emotion, color, onClick, language }) {
  return (
    <button 
      className="emotion-button" 
      style={{ backgroundColor: color.bg, color: color.text }} 
      onClick={onClick}
    >
      {emotionTranslations[language][emotion] || emotion}
    </button>
  );
}

function EmotionSelector({ emotions, level, parentEmotion, language }) {
  const navigate = useNavigate();
  const t = translations[language];

  if (!emotions || Object.keys(emotions).length === 0) {
    return <div>No more specific emotions available.</div>;
  }

  return (
    <div className="emotion-selector">
      <h2>
        {level === 0 ? t.chooseEmotion :
         level === 1 ? t.chooseMoreSpecific :
         t.chooseMostSpecific}
      </h2>
      <div className="emotion-buttons">
        {Object.keys(emotions).map(emotion => (
          <EmotionButton 
            key={emotion} 
            emotion={emotion} 
            color={getColor(emotion, parentEmotion)}
            onClick={() => {
              if (level === 2) {
                navigate(`/success/${parentEmotion}/${emotion}`);
              } else {
                navigate(`${emotion}`);
              }
            }}
            language={language}
          />
        ))}
      </div>
      {level > 0 && (
        <button className="back-button" onClick={() => navigate(-1)}>{t.startOver}</button>
      )}
    </div>
  );
}

function SuccessPage({ language }) {
  const { parentEmotion, emotion } = useParams();
  const navigate = useNavigate();
  const IconComponent = emotionIcons[parentEmotion] || FaMeh;
  const color = getColor(emotion, parentEmotion);
  const t = translations[language];

  return (
    <div className="success-page">
      <IconComponent size={64} className="emotion-icon" style={{ color: color.icon }} />
      <h2>{t.youAreFeeling}</h2>
      <h1 className="emotion-text" style={{ color: color.text }}>
        {emotionTranslations[language][emotion] || emotion}
      </h1>
      <button className="back-button" onClick={() => navigate('/')}>{t.startOver}</button>
    </div>
  );
}

function EmotionRoute({ language }) {
  const { emotion, subEmotion, subSubEmotion } = useParams();
  const level = subSubEmotion ? 2 : subEmotion ? 1 : emotion ? 0 : -1;

  let currentEmotions;
  let parentEmotion;
  if (level === -1) {
    currentEmotions = emotionsData;
  } else if (level === 0) {
    currentEmotions = emotionsData[emotion];
    parentEmotion = emotion;
  } else if (level === 1) {
    currentEmotions = emotionsData[emotion][subEmotion];
    parentEmotion = emotion;
  } else {
    currentEmotions = {};
    parentEmotion = emotion;
  }

  return <EmotionSelector emotions={currentEmotions} level={level + 1} parentEmotion={parentEmotion} language={language} />;
}

function App() {
  const [language, setLanguage] = useState('en');

  return (
    <Router>
      <div className="App">
        <button 
          className="language-toggle" 
          onClick={() => setLanguage(language === 'en' ? 'de' : 'en')}
        >
          {language === 'en' ? '🇩🇪' : '🇬🇧'}
        </button>
        <h1>{translations[language].emotionalWordWheel}</h1>
        <Routes>
          <Route path="/" element={<EmotionRoute language={language} />} />
          <Route path="/:emotion" element={<EmotionRoute language={language} />} />
          <Route path="/:emotion/:subEmotion" element={<EmotionRoute language={language} />} />
          <Route path="/:emotion/:subEmotion/:subSubEmotion" element={<EmotionRoute language={language} />} />
          <Route path="/success/:parentEmotion/:emotion" element={<SuccessPage language={language} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
